mapper.select:
SELECT * FROM (SELECT a.*, rownum rnum FROM (SELECT board_num,
REPLACE(REPLACE(title,'<','&lt;'),'>','&gt;') title,
hit, filename, reg_date, mem_num, id, nick_name, re_cnt, fav_cnt FROM spboard LEFT OUTER JOIN (SELECT COUNT(*) fav_cnt, board_num FROM spboard_fav GROUP BY board_num) USING (board_num) LEFT OUTER JOIN (SELECT COUNT(*) re_cnt, board_num FROM spboard_reply GROUP BY board_num) USING (board_num) JOIN spmember USING(mem_num)
)a)
WHERE rnum >= ${start} AND rnum <= #{end}

mapper.update:
UPDATE spboard SET
filename = #{filename},
title = #{title}, content = #{content}, ip = #{ip}, modify_date = SYSDATE WHERE board_num = #{board_num}
